				[TVSalesClassLocation] = CAST(ISNULL(CASE @IsOneTime WHEN 0 THEN CASE WHEN CD.T_USE_AGENCY_SETTING = 1 THEN AD.T_SALES_CLASS_LOCATION ELSE ISNULL(CD.T_SALES_CLASS_LOCATION, AD.T_SALES_CLASS_LOCATION) END ELSE OT.T_SALES_CLASS_LOCATION END, 1) AS int),
				[TVCampaignLocation] = CAST(ISNULL(CASE @IsOneTime WHEN 0 THEN CASE WHEN CD.T_USE_AGENCY_SETTING = 1 THEN AD.T_CAMPAIGN_LOCATION ELSE ISNULL(CD.T_CAMPAIGN_LOCATION, AD.T_CAMPAIGN_LOCATION) END ELSE OT.T_CAMPAIGN_LOCATION END, 1) AS int),
				[TVHeaderGroupBy] = CAST(ISNULL(CASE @IsOneTime WHEN 0 THEN CASE WHEN CD.T_USE_AGENCY_SETTING = 1 THEN AD.T_HEADER_GROUP_BY ELSE ISNULL(CD.T_HEADER_GROUP_BY, AD.T_HEADER_GROUP_BY) END ELSE OT.T_HEADER_GROUP_BY END, 0) AS int),
				[TVSortLinesBy] = CAST(ISNULL(CASE @IsOneTime WHEN 0 THEN CASE WHEN CD.T_USE_AGENCY_SETTING = 1 THEN AD.T_SORT_LINES_BY ELSE ISNULL(CD.T_SORT_LINES_BY, AD.T_SORT_LINES_BY) END ELSE OT.T_SORT_LINES_BY END, 1) AS int),
				[TVLineNumberColumn] = CAST(ISNULL(CASE @IsOneTime WHEN 0 THEN CASE WHEN CD.T_USE_AGENCY_SETTING = 1 THEN AD.T_LINE_NUMBER_INCL ELSE ISNULL(CD.T_LINE_NUMBER_INCL, AD.T_LINE_NUMBER_INCL) END ELSE OT.T_LINE_NUMBER_INCL END, 0) AS smallint),
				[TVShowZeroLineAmounts] = CAST(ISNULL(CASE @IsOneTime WHEN 0 THEN CASE WHEN CD.T_USE_AGENCY_SETTING = 1 THEN AD.T_SHOW_ZERO_LINE_AMOUNTS ELSE ISNULL(CD.T_SHOW_ZERO_LINE_AMOUNTS, AD.T_SHOW_ZERO_LINE_AMOUNTS) END ELSE OT.T_SHOW_ZERO_LINE_AMOUNTS END, 0) AS bit),
				[TVCloseDateColumn] = CAST(ISNULL(CASE @IsOneTime WHEN 0 THEN CASE WHEN CD.T_USE_AGENCY_SETTING = 1 THEN AD.T_CLOSE_DATE_INCL ELSE ISNULL(CD.T_CLOSE_DATE_INCL, AD.T_CLOSE_DATE_INCL) END ELSE OT.T_CLOSE_DATE_INCL END, 0) AS smallint),
				[TVUseAgencySetting] = CAST(ISNULL(CASE @IsOneTime WHEN 0 THEN CD.T_USE_AGENCY_SETTING ELSE 0 END, 0) AS bit),
				[TVCustomInvoiceID] = CAST(CASE @IsOneTime WHEN 0 THEN CASE WHEN CD.T_USE_AGENCY_SETTING = 1 THEN AD.T_CUSTOM_INVOICE_ID ELSE ISNULL(CD.T_CUSTOM_INVOICE_ID, AD.T_CUSTOM_INVOICE_ID) END ELSE OT.T_CUSTOM_INVOICE_ID END AS int)
			FROM 
				[dbo].[CLIENT] AS C LEFT OUTER JOIN 
				[dbo].[MEDIA_INV_DEF] AS CD ON CD.CL_CODE = C.CL_CODE CROSS JOIN 
				[dbo].[MEDIA_INV_DEF] AS AD CROSS JOIN 
				[dbo].[MEDIA_INV_DEF] AS OT
			WHERE 
				AD.CLIENT_OR_DEF = 1 AND 
				OT.CLIENT_OR_DEF = 0) AS IPS LEFT OUTER JOIN 
		[dbo].[advtf_location_query]() AS L ON L.LOC_ID = IPS.[LocationCode] INNER JOIN
		(SELECT FIELD_DESCRIPTION FROM [dbo].[GEN_DESC] WHERE FIELD_NAME = 'CITY_TAX') AS CTL ON 1 = 1 INNER JOIN
		(SELECT FIELD_DESCRIPTION FROM [dbo].[GEN_DESC] WHERE FIELD_NAME = 'COUNTY_TAX') AS COTL ON 1 = 1 INNER JOIN
		(SELECT FIELD_DESCRIPTION FROM [dbo].[GEN_DESC] WHERE FIELD_NAME = 'STATE_TAX') AS STL ON 1 = 1

END











