  	CREATE FUNCTION dbo.fn_IsUserInRole ( @UserName sysname, @RoleName sysname )  	RETURNS BIT  	AS  	BEGIN  		DECLARE @RetVal BIT  		SET @RetVal = 0    		SELECT @RetVal = 1  		 WHERE EXISTS ( SELECT *   				  FROM sysmembers mem   				  JOIN sysusers users   				    ON mem.memberuid = users.uid  				  JOIN sysusers groups   				    ON mem.groupuid = groups.uid  				 WHERE users.name = @UserName  				   AND groups.name = @RoleName )  	RETURN @RetVal  	END 