  CREATE FUNCTION [dbo].udf_ap_get_pmt_disc_amt (@al_ap_id INT)  RETURNS decimal(14,2)  /*Returns total payment amount for given AP */  AS  BEGIN  	DECLARE @ldec_prod_amt decimal(14,2)    	SET @ldec_prod_amt = (SELECT COALESCE(SUM(AP_CHK_AMT), 0) + COALESCE(SUM(AP_DISC_AMT), 0)  	FROM AP_PMT_HIST  	WHERE AP_ID=@al_ap_id)  	  	IF @ldec_prod_amt IS NULL  		SET @ldec_prod_amt = 0  	  	RETURN @ldec_prod_amt    END