  	CREATE FUNCTION [dbo].[udf_get_budget_acct]  	( @acct_type varchar(1), @office_code varchar(6), @sc_code varchar(6), @budget_type varchar(1) )  		RETURNS varchar(30) AS    	BEGIN  		DECLARE @acct_code varchar(30)    		IF @budget_type = 'M'  		BEGIN  			IF ( @sc_code IS NOT NULL )  				IF ( @acct_type = 'S' )  					SELECT @acct_code = ( SELECT MGLACODE_SALES FROM OFF_SC_ACCTS WHERE OFFICE_CODE = @office_code AND SC_CODE = @sc_code )			  				ELSE  					SELECT @acct_code = ( SELECT MGLACODE_COS FROM OFF_SC_ACCTS WHERE OFFICE_CODE = @office_code AND SC_CODE = @sc_code )	  			IF ( @acct_code IS NULL )  				IF ( @acct_type = 'S' )  					SELECT @acct_code = ( SELECT MGLACODE_SALES FROM OFFICE WHERE OFFICE_CODE = @office_code )			  				ELSE  					SELECT @acct_code = ( SELECT MGLACODE_COS FROM OFFICE WHERE OFFICE_CODE = @office_code )			  		END			  		ELSE  		BEGIN  		IF ( @sc_code IS NOT NULL )  			IF ( @acct_type = 'S' )  				SELECT @acct_code = ( SELECT PGLACODE_SALES FROM OFF_SC_ACCTS WHERE OFFICE_CODE = @office_code AND SC_CODE = @sc_code )			  			ELSE  				SELECT @acct_code = ( SELECT PGLACODE_COS FROM OFF_SC_ACCTS WHERE OFFICE_CODE = @office_code AND SC_CODE = @sc_code )	  		IF ( @acct_code IS NULL )  			IF ( @acct_type = 'S' )  				SELECT @acct_code = ( SELECT PGLACODE_SALES FROM OFFICE WHERE OFFICE_CODE = @office_code )			  			ELSE  				SELECT @acct_code = ( SELECT PGLACODE_COS FROM OFFICE WHERE OFFICE_CODE = @office_code )			  		END    	RETURN @acct_code  	END