  	CREATE FUNCTION [dbo].[udf_get_comp_open] ( @job_nbr integer )  		  	RETURNS integer AS  	BEGIN  		DECLARE @job_process_contrl integer  		DECLARE @job_open integer    		BEGIN  			SELECT @job_process_contrl = ( SELECT count(*) FROM JOB_COMPONENT WHERE JOB_NUMBER = @job_nbr AND JOB_PROCESS_CONTRL <> 6 AND JOB_PROCESS_CONTRL <> 12)  			IF ( @job_process_contrl > 0 )  				SELECT @job_open = 1  			ELSE  				SELECT @job_open = 0  		END  	RETURN @job_open  	END