  	CREATE FUNCTION [dbo].[udf_get_fnc_comm_flag] ( @fnc_code varchar(6) )  	RETURNS smallint AS  	BEGIN  		DECLARE @fnc_comm_flag smallint  		SELECT @fnc_comm_flag = ( SELECT CASE   						   WHEN COMM_INCL = 0 THEN NULL  						   WHEN COMMISSION = 0.000 THEN 0   						   WHEN COMMISSION IS NULL THEN NULL  						   ELSE 1   						 END  					    FROM dbo.BILLING_RATE br, dbo.BILL_RATE_PREC brp  					   WHERE br.BILL_RATE_PREC_ID = brp.BILL_RATE_PREC_ID  					     AND br.BILL_RATE_PREC_ID = 1  					     AND FNC_CODE = @fnc_code )   	RETURN @fnc_comm_flag  	END