  	CREATE FUNCTION [dbo].[udf_get_fnc_nobill_flag] ( @fnc_code varchar(6) )  	RETURNS smallint AS  	BEGIN  		DECLARE @fnc_nobill_flag smallint  		SELECT @fnc_nobill_flag = ( SELECT CASE   						     WHEN ( NONBILL_INCL = 0 ) THEN NULL  						     ELSE NOBILL_FLAG  						   END  					      FROM dbo.BILLING_RATE br, dbo.BILL_RATE_PREC brp  					     WHERE br.BILL_RATE_PREC_ID = brp.BILL_RATE_PREC_ID  					       AND br.BILL_RATE_PREC_ID = 1  					       AND FNC_CODE = @fnc_code )   	RETURN @fnc_nobill_flag  	END