  	CREATE FUNCTION [dbo].[udf_get_fnc_tax_flag] ( @fnc_code varchar(6) )  	RETURNS smallint AS  	BEGIN  		DECLARE @fnc_tax_flag smallint  		SELECT @fnc_tax_flag = ( SELECT CASE   						  WHEN ( TAX_INCL = 0 ) THEN NULL  						  ELSE TAX_FLAG  						END  					   FROM dbo.BILLING_RATE br, dbo.BILL_RATE_PREC brp  					  WHERE br.BILL_RATE_PREC_ID = brp.BILL_RATE_PREC_ID  					    AND br.BILL_RATE_PREC_ID = 1  					    AND FNC_CODE = @fnc_code )   		RETURN @fnc_tax_flag  		END