  	CREATE FUNCTION [dbo].[udf_get_prod_bill_emp_time] ( @cl_code varchar(6), @div_code varchar(6), @prod_code varchar(6))  	RETURNS smallint AS  	BEGIN  		DECLARE @bill_emp_time smallint  		SELECT @bill_emp_time = COALESCE(( SELECT CASE   						   	    WHEN ( NOBILL_FLAG = 0 ) THEN 1   						   	    WHEN ( NOBILL_FLAG = 1 ) THEN 0  				 		   	    ELSE NULL  						 	  END  					    	     FROM dbo.BILLING_RATE br, dbo.BILL_RATE_PREC brp  					   	    WHERE br.BILL_RATE_PREC_ID = brp.BILL_RATE_PREC_ID  					     	      AND br.BILL_RATE_PREC_ID = 28  					     	      AND CL_CODE = @cl_code   					     	      AND DIV_CODE = @div_code  					     	      AND PRD_CODE = @prod_code ), 1 )   	RETURN @bill_emp_time  	END