  	CREATE FUNCTION [dbo].[udf_get_prod_bill_rate] ( @cl_code varchar(6), @div_code varchar(6), @prod_code varchar(6) )  		  	RETURNS decimal(9,3) AS  	BEGIN  		DECLARE @prd_bill_rate decimal(9,3)  		SELECT @prd_bill_rate = ( SELECT BILLING_RATE   					    FROM dbo.BILLING_RATE   					   WHERE BILL_RATE_PREC_ID = 28   					     AND CL_CODE = @cl_code  					     AND DIV_CODE = @div_code  					     AND PRD_CODE = @prod_code )     	RETURN @prd_bill_rate  	END