  	CREATE FUNCTION [dbo].[udf_get_task_employee] ( @job_number integer, @job_component_nbr smallint, @seq_nbr smallint )  		  	RETURNS varchar(6) AS  	BEGIN  		DECLARE @emp_code varchar(6), @row_count smallint    		DECLARE emp_cursor CURSOR FOR  		 SELECT EMP_CODE  		   FROM dbo.JOB_TRAFFIC_DET_EMPS   		  WHERE JOB_NUMBER = @job_number  		    AND JOB_COMPONENT_NBR = @job_component_nbr  			AND SEQ_NBR = @seq_nbr  		FOR READ ONLY    		OPEN emp_cursor    		FETCH NEXT FROM emp_cursor INTO @emp_code  		IF ( @@FETCH_STATUS = 0 )  		BEGIN  			FETCH NEXT FROM emp_cursor INTO @emp_code  			IF ( @@FETCH_STATUS = 0 )  				SELECT @emp_code = ' ... '  		END    		CLOSE emp_cursor  		DEALLOCATE emp_cursor    	RETURN @emp_code  	END