  	CREATE FUNCTION [dbo].[udf_get_task_hours] ( @job_number integer, @job_component_nbr smallint, @seq_nbr smallint )  		  	RETURNS decimal(15,2) AS  	BEGIN  		DECLARE @hours_assigned decimal(15,2)    		SELECT @hours_assigned = ( SELECT COALESCE( SUM( COALESCE( HOURS_ALLOWED, 0.00 )), 0.00 )  					     FROM dbo.JOB_TRAFFIC_DET_EMPS   					    WHERE JOB_NUMBER = @job_number  					      AND JOB_COMPONENT_NBR = @job_component_nbr  					      AND SEQ_NBR = @seq_nbr )  	RETURN @hours_assigned  	END