  	CREATE FUNCTION [dbo].[udf_get_traffic_mgr] ( @job_number integer, @job_component_nbr smallint )  		  	RETURNS varchar(6) AS  	BEGIN  		DECLARE @emp_code varchar(6), @mgr_col varchar(20)  		BEGIN  			SELECT @mgr_col = ( SELECT CAST( AGY_SETTINGS_VALUE AS varchar(20) ) FROM dbo.AGY_SETTINGS WHERE AGY_SETTINGS_CODE = 'TRAFFIC_MGR_COL' )  			SELECT @emp_code = ( SELECT CASE @mgr_col   							WHEN 'TR_TITLE1' THEN ASSIGN_1   							WHEN 'TR_TITLE2' THEN ASSIGN_2   							WHEN 'TR_TITLE3' THEN ASSIGN_3  							WHEN 'TR_TITLE4' THEN ASSIGN_4   							WHEN 'TR_TITLE5' THEN ASSIGN_5  							ELSE NULL  						    END  					       FROM dbo.JOB_TRAFFIC   					      WHERE JOB_NUMBER = @job_number   						AND JOB_COMPONENT_NBR = @job_component_nbr )   		END	  	RETURN @emp_code  	END