CREATE PROCEDURE [dbo].[usp_News_Update]
	@NewsID int,
	@NewsDate datetime,
	@NewsTitle varchar(100),
	@NewsText varchar(500),
	@Active bit
AS

UPDATE [dbo].[tblNews] SET
	[NewsDate] = @NewsDate,
	[NewsTitle] = @NewsTitle,
	[NewsText] = @NewsText,
	[Active] = @Active
WHERE
	[NewsID] = @NewsID

--endregion

















