  	CREATE VIEW dbo.V_AP_AB_JOBS (JOB_NUMBER, JOB_COMPONENT_NBR)  AS    		SELECT AB.JOB_NUMBER, AB.JOB_COMPONENT_NBR  		  FROM dbo.ADVANCE_BILLING AB, dbo.AP_PRODUCTION AP   		 WHERE AB.JOB_NUMBER = AP.JOB_NUMBER   		   AND AB.JOB_COMPONENT_NBR = AP.JOB_COMPONENT_NBR  	      GROUP BY AB.JOB_NUMBER, AB.JOB_COMPONENT_NBR  		HAVING SUM( AB.ADV_BILL_NET_AMT ) <> 0 