  	CREATE VIEW dbo.V_AP_MEDIA_RADIO (AP_ID, ORDER_NBR, BRDCAST_YEAR, BRDCAST_MONTH) AS    	     SELECT AP_ID, ORDER_NBR, BRDCAST_YEAR, BRDCAST_MONTH   	       FROM dbo.AP_RADIO AA  	   GROUP BY AP_ID, ORDER_NBR, BRDCAST_YEAR, BRDCAST_MONTH  	     HAVING COALESCE(SUM(EXT_NET_AMT), 0.00) + COALESCE(SUM(VENDOR_TAX), 0.00) <> 0 