  	CREATE VIEW dbo.V_AP_PROD_ID (AR_INV_NBR, AR_INV_DATE, AR_POST_PERIOD, AR_INV_AMOUNT)  AS    	     SELECT ACCT_REC.AR_INV_NBR, ACCT_REC.AR_INV_DATE, ACCT_REC.AR_POST_PERIOD, ACCT_REC.AR_INV_AMOUNT  	       FROM dbo.ACCT_REC  	      WHERE (ACCT_REC.VOID_FLAG=0 Or ACCT_REC.VOID_FLAG Is Null)   		AND (ACCT_REC.MANUAL_INV=0 Or ACCT_REC.MANUAL_INV IS NULL)  	   GROUP BY ACCT_REC.AR_INV_NBR, ACCT_REC.AR_INV_DATE, ACCT_REC.AR_POST_PERIOD, ACCT_REC.AR_INV_AMOUNT 