  	CREATE VIEW dbo.V_AP_PROD_ID2 (AP_ID, JOB_NUMBER, JOB_COMPONENT_NBR) AS  	     SELECT AP_PRODUCTION.AP_ID, AP_PRODUCTION.JOB_NUMBER, AP_PRODUCTION.JOB_COMPONENT_NBR  	       FROM dbo.AP_PRODUCTION   	      WHERE (MODIFY_DELETE IS NULL OR MODIFY_DELETE = 0)  		AND EXISTS (SELECT AR_INV_NBR   			      FROM dbo.V_AP_PROD_ID  			     WHERE AP_PRODUCTION.AR_INV_NBR = V_AP_PROD_ID.AR_INV_NBR )  	   GROUP BY AP_PRODUCTION.AP_ID, AP_PRODUCTION.JOB_NUMBER, AP_PRODUCTION.JOB_COMPONENT_NBR  	     HAVING COALESCE(SUM(AP_PROD_EXT_AMT), 0.00) + COALESCE(SUM(EXT_NONRESALE_TAX), 0.00) <> 0 