  	CREATE VIEW dbo.V_AP_PROD_ID3(JOB_NUMBER, JOB_COMPONENT_NBR, AR_INV_NBR) AS  	     SELECT ARINV_JOB.JOB_NUMBER, ARINV_JOB.JOB_COMPONENT_NBR,   		    ARINV_JOB.AR_INV_NBR  	       FROM dbo.ARINV_JOB  	      WHERE EXISTS (SELECT JOB_NUMBER   			      FROM dbo.V_AP_PROD_ID2 V2  			     WHERE ARINV_JOB.JOB_NUMBER = V2.JOB_NUMBER  			       AND ARINV_JOB.JOB_COMPONENT_NBR = V2.JOB_COMPONENT_NBR)  	   GROUP BY ARINV_JOB.JOB_NUMBER, ARINV_JOB.JOB_COMPONENT_NBR, ARINV_JOB.AR_INV_NBR 