  	CREATE VIEW dbo.V_CR_CLIENT_DTL (AR_INV_NBR, CR_CASH_AMT) AS  	     SELECT AR_INV_NBR, COALESCE(SUM(CR_PAY_AMT), 0.00) + COALESCE(SUM(CR_ADJ_AMT), 0.00)  	       FROM dbo.CR_CLIENT_DTL  	   GROUP BY AR_INV_NBR  	     HAVING (COALESCE(SUM(CR_PAY_AMT), 0.00) + COALESCE(SUM(CR_ADJ_AMT), 0.00)) <> 0 