  CREATE VIEW dbo.V_DEST_CONTACT ( DESTINATION_CODE, CL_CODE, DIV_CODE, PRD_CODE, DEST_CONT_CODE, DEST_CONT_FML, DEST_CONT_LF, DEST_CONT_FIM, DC_INACTIVE_FLAG ) AS  	        SELECT DESTINATION_CODE, CL_CODE, DIV_CODE, PRD_CODE, DEST_CONT_CODE,  	    COALESCE( RTRIM( DEST_CONT_FNAME ) + ' ', '' ) + COALESCE( DEST_CONT_MI + '. ', '' ) + COALESCE( DEST_CONT_LNAME, '' ),   		  	    COALESCE( DEST_CONT_LNAME + ', ', '' ) + COALESCE( RTRIM( DEST_CONT_FNAME ), '' ),   		  	    COALESCE( DEST_CONT_FNAME + '. ', '' ) + COALESCE( RTRIM( DEST_CONT_LNAME ), '' ),   		  	    INACTIVE_FLAG   	            FROM dbo.DESTINATION_CONTACT 