  	CREATE VIEW [dbo].[V_MAG_HEADER] (   		ORDER_NBR, ORDER_DESC, CL_CODE, DIV_CODE, PRD_CODE, CMP_CODE, MEDIA_TYPE, VN_CODE, VR_CODE, VR_CODE2,  		CLIENT_PO, STATUS, ORDER_DATE, BUYER, ORDER_COMMENT, HOUSE_COMMENT, PUB, REP1, REP2, CREATE_DATE, USER_ID,   		BILL_COOP_CODE, ORD_PROCESS_CONTRL, CMP_IDENTIFIER, LINK_ID, RECONCILE_FLAG, OFFICE_CODE, SOURCE, LOCKED )      	AS      	 SELECT ORDER_NBR, ORDER_DESC, CL_CODE, DIV_CODE, PRD_CODE, CMP_CODE, MEDIA_TYPE, VN_CODE, VR_CODE, VR_CODE2,  		CLIENT_PO, STATUS, ORDER_DATE, BUYER, ORDER_COMMENT, HOUSE_COMMENT, PUB, REP1, REP2, CREATE_DATE, USER_ID,   		BILL_COOP_CODE, ORD_PROCESS_CONTRL, CMP_IDENTIFIER, LINK_ID, RECONCILE_FLAG, OFFICE_CODE, 'OLD', NULL AS LOCKED       	   FROM dbo.MAG_HEADER      	UNION ALL     	 SELECT	ORDER_NBR, ORDER_DESC, CL_CODE, DIV_CODE, PRD_CODE, CMP_CODE, MEDIA_TYPE, VN_CODE, VR_CODE, VR_CODE2,     	  		CLIENT_PO, STATUS, ORDER_DATE, BUYER, ORDER_COMMENT, HOUSE_COMMENT, PUB, REP1, REP2, CREATE_DATE, USER_ID,   		BILL_COOP_CODE, ORD_PROCESS_CONTRL, CMP_IDENTIFIER, LINK_ID, RECONCILE_FLAG, OFFICE_CODE, 'NEW', LOCKED   	   FROM dbo.MAGAZINE_HEADER 