                      dbo.TV_GL AS g ON d.ORDER_NBR = g.ORDER_NBR AND d.REV_NBR = g.REV_NBR AND d.LINE_NBR = g.LINE_NBR AND 
                      d.SEQ_NBR = g.SEQ_NBR AND d.BRDCAST_YEAR = g.BRDCAST_YEAR INNER JOIN
                      dbo.TV_HEADER AS h ON g.ORDER_NBR = h.ORDER_NBR AND g.REV_NBR = h.REV_NBR
