SELECT     d.ORDER_NBR, d.LINE_NBR, d.REV_NBR, d.SEQ_NBR, d.OCT_LINE_NET, d.OCT_COMM_AMT, d.OCT_REBATE_AMT, d.OCT_DISCOUNT, 
                      d.OCT_VENDOR_TAX, d.OCT_STATE_TAX, d.OCT_COUNTY_TAX, d.OCT_CITY_TAX, d.OCT_SPOTS, d.OCT_BILLING_AMT, d.OCT_AR_INV_NBR, 
                      d.OCT_AR_TYPE, d.OCT_AR_INV_SEQ, 'OCT' AS EXPR1, d.BRDCAST_YEAR, d.BILLING_USER, 'TV' AS EXPR2, g.OCT_GLACODE_CITY, 
                      g.OCT_GLACODE_COS, g.OCT_GLACODE_COUNTY, g.OCT_GLACODE_SALES, g.OCT_GLACODE_STATE, g.OCT_GLACODE_WIP, g.OCT_GLESEQ_CITY, 
                      g.OCT_GLESEQ_COS, g.OCT_GLESEQ_COUNTY, g.OCT_GLESEQ_SALES, g.OCT_GLESEQ_STATE, g.OCT_GLESEQ_WIP, g.OCT_GLEXACT, 
                      g.OCT_GLEXACT_DEF, d.RECONCILE_LINE, d.DO_NOT_BILL, h.BILL_TYPE_FLAG, d.BRDCAST_YEAR * 100 + 10 AS BRDCAST_PERIOD
