                      dbo.TV_GL AS g ON d.ORDER_NBR = g.ORDER_NBR AND d.REV_NBR = g.REV_NBR AND d.LINE_NBR = g.LINE_NBR AND 
                      d.SEQ_NBR = g.SEQ_NBR AND d.BRDCAST_YEAR = g.BRDCAST_YEAR INNER JOIN
                      dbo.TV_HEADER AS h ON g.ORDER_NBR = h.ORDER_NBR AND g.REV_NBR = h.REV_NBR
UNION ALL
SELECT     d.ORDER_NBR, d.LINE_NBR, d.REV_NBR, d.SEQ_NBR, d.NOV_LINE_NET, d.NOV_COMM_AMT, d.NOV_REBATE_AMT, d.NOV_DISCOUNT, 
                      d.NOV_VENDOR_TAX, d.NOV_STATE_TAX, d.NOV_COUNTY_TAX, d.NOV_CITY_TAX, d.NOV_SPOTS, d.NOV_BILLING_AMT, d.NOV_AR_INV_NBR, 
                      d.NOV_AR_TYPE, d.NOV_AR_INV_SEQ, 'NOV' AS EXPR1, d.BRDCAST_YEAR, d.BILLING_USER, 'TV' AS EXPR2, g.NOV_GLACODE_CITY, 
                      g.NOV_GLACODE_COS, g.NOV_GLACODE_COUNTY, g.NOV_GLACODE_SALES, g.NOV_GLACODE_STATE, g.NOV_GLACODE_WIP, 
                      g.NOV_GLESEQ_CITY, g.NOV_GLESEQ_COS, g.NOV_GLESEQ_COUNTY, g.NOV_GLESEQ_SALES, g.NOV_GLESEQ_STATE, g.NOV_GLESEQ_WIP, 
                      g.NOV_GLEXACT, g.NOV_GLEXACT_DEF, d.RECONCILE_LINE, d.DO_NOT_BILL, h.BILL_TYPE_FLAG, 
