SELECT     d .ORDER_NBR, d .LINE_NBR, d .REV_NBR, d .SEQ_NBR, d .DEC_LINE_NET, d .DEC_COMM_AMT, d .DEC_REBATE_AMT, d .DEC_DISCOUNT, 
                      d .DEC_VENDOR_TAX, d .DEC_STATE_TAX, d .DEC_COUNTY_TAX, d .DEC_CITY_TAX, d .DEC_SPOTS, d .DEC_BILLING_AMT, d .DEC_AR_INV_NBR, 
