  	CREATE VIEW dbo.V_VN_CONTACT ( VN_CODE, VC_CODE, VC_FML, VC_LF, VC_INACTIVE_FLAG )  	AS  	 SELECT VN_CODE, VC_CODE,   		COALESCE( RTRIM( VC_FNAME ) + ' ', '' ) + COALESCE( VC_MI + '. ', '' ) + COALESCE( VC_LNAME, '' ),   		COALESCE( VC_LNAME + ', ', '' ) + COALESCE( RTRIM( VC_FNAME ), '' ),   		VC_INACTIVE_FLAG   	   FROM VEN_CONT 